b:if.script:
//
(function() {
  'use strict';
  
  // ========================================
  // FIREBASE CONFIG - KENDİ BİLGİLERİNLE DEĞİŞTİR
  // ========================================
  var firebaseConfig = {
    apiKey: "YOUR_API_KEY",
    authDomain: "YOUR_PROJECT.firebaseapp.com",
    projectId: "YOUR_PROJECT",
    storageBucket: "YOUR_PROJECT.firebasestorage.app",
    messagingSenderId: "YOUR_SENDER_ID",
    appId: "YOUR_APP_ID"
  };
  // ========================================
  
  function initBloggerComments() {
    // BloggerComments yüklendi mi kontrol et
    if (typeof window.BloggerComments === 'undefined') {
      console.error('[BloggerComments] Library not loaded!');
      return;
    }

    // Container var mı kontrol et
    var container = document.getElementById('blogger-comments-container');
    if (!container) {
      console.error('[BloggerComments] Container not found!');
      return;
    }

    try {
      // Widget oluştur ve başlat
      var widget = new window.BloggerComments(container, {
        firebaseConfig: firebaseConfig,
        postId: window.location.pathname,
        postTitle: document.title,
        adminEmails: ['admin@example.com'], // Admin email adreslerini değiştir
        maxCommentLength: 2000,
        maxReplyDepth: 3
      });
      
      widget.init();
      console.log('[BloggerComments] Initialized successfully!');
      
    } catch (error) {
      console.error('[BloggerComments] Init error:', error);
    }
  }

  // Sayfa yüklenince başlat
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initBloggerComments);
  } else {
    setTimeout(initBloggerComments, 100);
  }
})();
//
